import { Offer1 } from "./offers/Offer1";
import styled from "styled-components";
import { Filters } from "./Filters/Filters";
import { HotelInfo } from "./HotelInfo/HotelInfo";
import { TopFilterBar } from "./Filters/TopFilterBar";
import { FooterTwo } from "./FooterTwo";
import { COVID } from "./COVID";
import { hotels } from "./db";
import { useState, useEffect } from "react";
import NavbarSticky from "./Home/NavbarSticky";

const MainDiv = styled.div`
  display: flex;
  width: 100%;
`;
const LeftDiv = styled.div`
  display: flex;
  flex-direction: column;
  padding: 2%;
  width: 25%;
  position: sticky;
  position: -webkit-sticky;
`;
const RightDiv = styled.div`
  display: flex;
  flex-direction: column;
  position: relative;
  width: 74%;
  padding: 2%;
`;

const Div = styled.div`
  & header > img {
    padding: 20px;
  }
`;

export const HotelOptions = () => {
  const [cardData, setCardData] = useState([]);

  useEffect(() => {
    const data = hotels;
    // console.log(hotels.hotel);
    setCardData(data.hotel);
  }, []);
  return (
    <div style={{ position: "realtive" }}>
      <Div
        style={{
          position: "fixed",
          zIndex: "999999999",
          backgroundColor: "#fff",
          width: "100%",
          height: "70px",
        }}
      >

        <NavbarSticky />
      </Div>
      <div style={{ position: "absolute", marginTop: "75px" }}>
        <COVID />
        <MainDiv>
          <LeftDiv>
            <Filters />
          </LeftDiv>
          <RightDiv>
            <TopFilterBar />
            <Offer1 />
            {cardData.map((e) => {
              return <HotelInfo key={e.id} data={e} />;
            })}
          </RightDiv>
        </MainDiv>
        <FooterTwo />
      </div>
    </div>
  );
};
